import React, { useState } from 'react';

// Συνάρτηση για την απόκτηση ή δημιουργία session ID
const getSessionId = () => {
    // Εδώ θα μπορούσατε να επιστρέφετε ένα πραγματικό session ID αν χρησιμοποιείτε κάποιον μηχανισμό authentication
    return 'some-session-id';
};

const SubmitProblemForm = () => {
    const [description, setDescription] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        const sessionId = getSessionId(); // Χρησιμοποιεί την συνάρτηση getSessionId

        try {
            const response = await fetch('http://localhost:3007/submit-problem', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ sessionId, description }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok.');
            }

            // Μπορείτε να προσθέσετε επιπλέον λογική μετά την επιτυχή αποστολή, π.χ., εμφάνιση μηνύματος
            console.log('Problem submitted successfully');

        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
        }

        setDescription(''); // Επαναφορά της τιμής του description
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Problem Description:
                <textarea
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    required
                />
            </label>
            <button type="submit">Submit Problem</button>
        </form>
    );
};

export default SubmitProblemForm;
